import React, {useState}from 'react';
import QrReader from 'react-qr-reader';
import "../styles/App.css";

const App = () => {
  //cambio de states a hook
  const [Qr, setQr] = useState({
    result: 'Escanea tu QR'
  })

  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  const handleScan = data => {
    if (data) {
      setQr({
        result: data
      })
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <React.Fragment>
    <header>

      <img src='http://baluher.com/wp-content/uploads/2018/04/baluher_logo.png' alt="I'm an easter egg" />
      <h1>Tech demo</h1>
    </header>

    <center>
      <QrReader
        delay={300}
        onError={handleError}
        onScan={handleScan}
        style={{ width: '30%' }}
      />

      <h2>{Qr.result}</h2>
    </center>
    </React.Fragment>
  )
}


export default App;
