import React, {useState}from 'react';
import QrReader from 'react-qr-reader';

const App = () => {
  //cambio de states a hook
  const [Qr, setQr] = useState({
    result: '<h2>Escanea tu QR</h2>'
  })

  const handleScan = data => {
    if (data) {
      setQr({
        result: data
      })
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <React.Fragment>
    <h1>Tech demo</h1>
    <QrReader
      delay={300}
      onError={handleError}
      onScan={handleScan}
      style={{ width: '30%' }}
    />
    <p>{Qr.result}</p>
    </React.Fragment>
  )
}


export default App;
